import React, { useState, useCallback } from 'react';
import axios from 'axios';

function ProductRegistration() {
  const [productData, setProductData] = useState({
    productName: '',
    productPrice: '',
    // ... 다른 상품 관련 상태들
  });

  const changeProductData = useCallback((evt) => {
    const { name, value } = evt.target;
    setProductData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  }, []);

  const handleProductRegistration = async (e) => {
    e.preventDefault();

    try {
      // 서버에 상품 등록 요청을 보냄
      const response = await axios.post('http://localhost:8000/addProduct', productData);

      // 성공적으로 등록되면 어떤 작업 수행
      console.log('상품 등록 성공:', response.data);
      // ... 상태 초기화 또는 다른 작업 수행
    } catch (error) {
      // 등록 실패 시 에러 처리
      console.error('상품 등록 실패:', error);
    }
  };

  return (
    <main>
      <section>
        <div className="container">
          <div className="row">
            <div className="col-md-12">
              <div className="title-wrap d-flex justify-content-between">
                <div className="title-box">
                  <h2 className="title-a">상품 등록</h2>
                  <h2 className="title-a">상품 등록</h2>
                  <h2 className="title-a">상품 등록</h2>
                  <h2 className="title-a">상품 등록</h2>
                  <h2 className="title-a">상품 등록</h2>
                  <h2 className="title-a">상품 등록</h2>

                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <form className="col-sm-12" onSubmit={handleProductRegistration}>
              <div className="col-sm-12 position-relative form-group mb-3">
                <label htmlFor="productName" className="form-label">상품명</label>
                <input
                  type="text"
                  className="form-control"
                  id="productName"
                  name="productName"
                  value={productData.productName}
                  onChange={changeProductData}
                />
              </div>
              {/* ... 다른 상품 정보 입력란 */}
              <div className="col-sm-12 position-relative form-group">
                <button type="submit" className="btn btn-danger btn-sm">등록</button>{' '}
                <button type="reset" className="btn btn-primary btn-sm">초기화</button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </main>
  );
}

export default ProductRegistration;
