import React from 'react';
import { useNavigate } from 'react-router-dom';

const buttonStyle = {
  backgroundColor: 'orange',
  border: 'none',
  borderRadius: '8px',
  padding: '15px 30px',
  textDecoration: 'none',
  color: 'white',
  fontSize: '1.5em',
  transition: 'background-color 0.3s ease',
};

const hoverStyle = {
  backgroundColor: 'lightgray',
};

function PRbutton() {
  const [isHovered, setIsHovered] = React.useState(false);
  const navigate = useNavigate();
  const [isUserLoggedIn, setIsUserLoggedIn] = React.useState(false); // 로그인 상태 상태값

  // 실제 로그인 상태를 확인하는 함수
  const checkLoginStatus = () => {
    // 여기서 실제 로그인 상태를 확인하는 로직을 구현합니다.
    // 예를 들어, 토큰이 유효한지 확인하거나 세션을 체크하는 등의 방법으로 로그인 상태를 판단합니다.
    const isLoggedIn = 200/* 실제로 로그인되어 있는지 여부를 판단하는 코드 */
    setIsUserLoggedIn(isLoggedIn);
  };

  // 컴포넌트가 마운트될 때 로그인 상태 확인
  React.useEffect(() => {
    checkLoginStatus();
  }, []);

  // 상품 등록 버튼 클릭 핸들러
  const handleClick = () => {
    // 로그인되어 있지 않은 경우
    if (!isUserLoggedIn) {
      const confirmed = window.confirm('로그인이 필요합니다. 로그인 하시겠습니까?');
      if (confirmed) {
        navigate('/login'); // 로그인 페이지 경로로 리다이렉트
      }
    } else {
      navigate('/product-register'); // 로그인된 경우 상품 등록 페이지로 이동
    }
  };

  return (
    <div>
      <button
        style={{ ...buttonStyle, ...(isHovered && hoverStyle) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onClick={handleClick}
      >
        상품 등록
      </button>
    </div>
  );
}

export default PRbutton;
