import React, { useEffect, useState } from 'react';

function ProductRegistration() {
  // 상태 정의 및 초기화
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  // ... (나머지 상태들도 동일하게 초기화)

  // 상품 목록 불러오기
  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem('products'));
    if (storedProducts) {
      setProducts(storedProducts);
    }
  }, []);

  // 상품 목록 로컬 스토리지에 저장
  const saveProductsToLocalStorage = (updatedProducts) => {
    localStorage.setItem('products', JSON.stringify(updatedProducts));
  };

  // 상품 등록 이벤트 처리
  const handleProductRegistration = (e) => {
    e.preventDefault();

    // 새로운 상품 정보 생성
    const newProduct = {
      name: productName,
      price: productPrice,
      // ... (나머지 상품 정보도 동일하게 추가)
    };

    // 상품 정보 업데이트 및 로컬 스토리지에 저장
    const updatedProducts = [...products, newProduct];
    setProducts(updatedProducts);
    saveProductsToLocalStorage(updatedProducts);

    setSubmittedProduct(newProduct); // 등록한 상품 정보를 submittedProduct에 저장

    // 상품 등록 후 입력 필드 초기화
    setProductName('');
    setProductPrice('');
    // ... (나머지 입력 필드도 동일하게 초기화)
  };

  // ... (handleImageChange 함수와 handleCancel 함수는 기존과 동일)

  return (
    <>
      {/* ... (기존의 코드와 동일) */}
      {/* 상품 등록 후에 등록된 상품 정보를 화면에 표시 */}
      <div className="container-fluid mt-5">
        <h2 className="text-center mb-4">등록한 상품 정보</h2>
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="border p-4">
              <h4>상품명: {submittedProduct.name}</h4>
              <p>정가: {submittedProduct.price}</p>
              {/* ... (나머지 상품 정보도 동일하게 추가) */}
              <img
                src={submittedProduct.image ? submittedProduct.image : 'img/sale.png'}
                alt="Product"
                className="img-fluid mt-3"
                style={{ maxWidth: '100%', maxHeight: '300px', objectFit: 'cover' }}
              />
            </div>
          </div>
        </div>
      </div>

      {/* 상품 목록 표시 */}
      <h2>상품 목록</h2>
      <div className="container-fluid fruite py-5">
        <div className="container py-5">
          <div className="row g-4 justify-content-center">
            {products.map((product, index) => (
              <div className="col-md-6 col-lg-6 col-xl-4" key={index}>
                <div className="rounded position-relative fruite-item">
                  <div className="fruite-img">
                    <img src={product.image} className="img-fluid w-100 rounded-top" alt={product.name} />
                  </div>
                  <div className="p-4 border border-secondary border-top-0 rounded-bottom">
                    <h4>{product.name}</h4>
                    <p>{product.description}</p>
                    <div className="d-flex justify-content-between flex-lg-wrap">
                      <p className="text-dark fs-5 fw-bold mb-0">P {product.price}</p>
                      <a href="#" className="btn border border-secondary rounded-pill px-3 text-primary">
                        <i className="me-2 text-primary"></i>구매하기
                      </a>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default ProductRegistration;
