import React, { useCallback, useState } from 'react';
import axios from 'axios';

function ProductRegister() {
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productBarcode, setProductBarcode] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productImage, setProductImage] = useState(null);
  const [productDiscount, setProductDiscount] = useState('');
  const [productExpiration, setProductExpiration] = useState('');
  const [finalPrice, setFinalPrice] = useState('');
  const [submittedProduct, setSubmittedProduct] = useState('');

  const [data, setData] = useState({
    productName: '',
    productPrice: '',
    productBarcode: '',
    productDescription: '',
    productImage: '',
    productDiscount: '',
    productExpiration: '',
    finalPrice: ''
  });

  const changeData = useCallback((evt) => {
    setData((data) => ({ ...data, [evt.target.name]: evt.target.value }));
  }, []);

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setProductImage(URL.createObjectURL(selectedImage));
  };

  const handleProductRegistration = useCallback(async (evt) => {
    evt.preventDefault();

    const newProduct = {
      name: data.productName,
      price: data.productPrice,
      barcode: data.productBarcode,
      description: data.productDescription,
      image: data.productImage,
      discount: data.productDiscount,
      expiration: data.productExpiration,
      finalPrice: data.finalPrice,
    };

    const response = await axios.post('http://localhost:8000/products/register', newProduct);
    console.log(response);
    if (response.data.code === 200) {
      setSubmittedProduct(newProduct);
    } else {
      // Handle error
    }
  }, [data]);

  return (
    <>
      {/* 상단 레이아웃 및 타이틀 부분은 Login 컴포넌트와 동일하게 작성 */}
      <div className="container-fluid py-5">
        {/* ... (상단 레이아웃 및 타이틀 부분 작성) */}
      </div>

      {/* 상품 등록 폼 */}
      <div className="container-fluid py-5 mt-5 d-flex justify-content-center">
        <div className="border p-4" style={{ maxWidth: '800px' }}>
          <div className="row g-4">
            <div className="col-lg-6 d-flex flex-column justify-content-center align-items-center">
              {/* 이미지 업로드 부분 */}
              {/* ... (이미지 업로드 input 태그 및 미리보기 이미지 부분 작성) */}
            </div>
            <div className="col-lg-6">
              {/* 상품 정보 입력 폼 */}
              <form onSubmit={handleProductRegistration}>
                {/* ... (입력 필드 및 상품 정보 관련 부분 작성) */}
                <button
                  type="submit"
                  className="btn border-primary py-3 px-4 text-uppercase text-primary"
                  style={{marginTop:'5px', marginBottom:'30px', width: '10%'}}>
                  등록하기
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>

      {/* 등록한 상품 정보 표시 */}
      <div className="container-fluid mt-5">
        <h2 className="text-center mb-4">등록한 상품 정보</h2>
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <div className="border p-4">
              {/* 등록한 상품 정보 표시 부분 작성 */}
              {/* ... (등록한 상품 정보 출력 부분 작성) */}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProductRegister;
