import React, { useState } from 'react';
import HeartIcon from './HeartIcon';

function ProductDetail() {
  const [showModal, setShowModal] = useState(false);

  const openPaymentModal = () => {
    setShowModal(true);
  };

  const closePaymentModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      {/* 구매하기 버튼 */}
      <p className="btn border border-secondary rounded-pill px-4 py-2 mb-4 text-primary" onClick={openPaymentModal}>
        <i className="me-2 text-primary"><HeartIcon /></i> 구입하기
      </p>

      {/* 결제 모달 */}
      {showModal && (
        <div className="modal" style={{ display: showModal ? 'block' : 'none' }}>
          <div className="modal-content p-0">
            <span className="close" onClick={closePaymentModal}>&times;</span>

            <div className="container">
              <div className="row g-4 justify-content-center">
                <div className="col-sm-8 col-md-7 col-lg-6 col-xl-4 bg-light rounded p-4">
                  <h1 className="display-6 mb-4">Cart <span className="fw-normal">Total</span></h1>
                  <div className="d-flex justify-content-between mb-4">
                    <h5 className="mb-0 me-4">Subtotal:</h5>
                    <p className="mb-0">$96.00</p>
                  </div>
                  <div className="d-flex justify-content-between">
                    <h5 className="mb-0 me-4">Shipping</h5>
                    <div>
                      <p className="mb-0">Flat rate: $3.00</p>
                    </div>
                  </div>
                  <p className="mb-0 text-end">Shipping to Ukraine.</p>

                  <div className="py-4 border-top border-bottom d-flex justify-content-between">
                    <h5 className="mb-0 ps-4 me-4">Total</h5>
                    <p className="mb-0 pe-4">$99.00</p>
                  </div>
                  <button className="btn border-secondary rounded-pill px-4 py-3 text-primary text-uppercase ms-4" type="button">Proceed Checkout</button>
                </div>
              </div>
            </div>
            <button onClick={closePaymentModal} className="btn border-secondary rounded-1 w-100 py-3 text-primary text-uppercase" type="button">닫기</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetail;
