import React, { useState } from 'react';
import HeartIcon from './HeartIcon';

function ProductDetail() {
  const [showModal, setShowModal] = useState(false);

  const openPaymentModal = () => {
    setShowModal(true);
  };

  const closePaymentModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      {/* 구매하기 버튼 */}
          <a href="#" class="btn border border-secondary rounded-pill px-4 py-2 mb-4 text-primary" onClick={openPaymentModal}><i class="me-2 text-primary"><HeartIcon /></i> 구입하기</a>
      {/* <button onClick={openPaymentModal}>구매하기</button> */}

      {/* 결제 모달 */}
      {showModal && (
        <div className="modal" style={{ display: showModal ? 'block' : 'none' }}>
          <div className="modal-content">
            <span className="close" onClick={closePaymentModal}>&times;</span>
            <h2>결제 정보 입력</h2>
            {/* ... (결제창의 내용 및 입력 폼 등을 추가하세요) */}
            <button onClick={closePaymentModal}>닫기</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetail;
