import React, { useEffect, useState } from 'react';

function ProductRegistration() {
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productBarcode, setProductBarcode] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productImage, setProductImage] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const storedProducts = JSON.parse(localStorage.getItem('products'));
    if (storedProducts) {
      setProducts(storedProducts);
    }
  }, []);

  const saveProductsToLocalStorage = (updatedProducts) => {
    localStorage.setItem('products', JSON.stringify(updatedProducts));
  };

  const handleProductRegistration = (e) => {
    e.preventDefault();

    const newProduct = {
      name: productName,
      price: productPrice,
      barcode: productBarcode,
      description: productDescription,
      image: productImage,
    };

    const updatedProducts = [...products, newProduct];
    setProducts(updatedProducts);
    saveProductsToLocalStorage(updatedProducts);

    setProductName('');
    setProductPrice('');
    setProductBarcode('');
    setProductDescription('');
    setProductImage(null);
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setProductImage(URL.createObjectURL(selectedImage));
  };
  return (
    <div>
      <h1>상품등록</h1>
      <h1>상품등록</h1>

        {/* <!-- Single Product Start --> */}
        <div class="container-fluid py-5 mt-5">
            {/* < class="container py-5"> */}
                <div class="row g-4 mb-5">
                    <div class="col-lg-8 col-xl-9">
                        <div class="row g-4">
                            <div class="col-lg-6">
                                <div class="border rounded">
                                    <a href="#">
                                        <img src="img/single-item.jpg" class="img-fluid rounded" alt="Image" />
                                    </a>
  
			<h1 class="display-3">판매하기</h1>
		</div>
	</div>
	<div class="container">	
		<form name="newProduct" action="./processAddProduct.jsp" class="form-horizontal" 
		method="post">
			<div class="form-group row">
				<label class="col-sm-2">상품명</label>
				<div class="col-sm-3">
					<input type="text" id="bookID"  name="bookID"  class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">상품정가</label>
				<div class="col-sm-3">
					<input type="text" id="bookName"  name="bookName" class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">할인율</label>
				<div class="col-sm-3">
					<input type="text" id="unitPrice"  name="unitPrice" class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">바코드 번호</label>
				<div class="col-sm-3">
					<input type="text" name="publisher" class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">유효기간</label>
				<div class="col-sm-3">
					<input type="text" name="author" class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">설명</label>
				<div class="col-sm-5">
					<textarea name="description" cols="50" rows="2" class="form-control"></textarea>
				</div>
			</div>
			<div class="form-group row">
				<label class="col-sm-2">최종가격</label>
				<div class="col-sm-3">
					<input type="text" name="category" class="form-control" />
				</div>
			</div>
			<div class="form-group row">
				<div class="col-sm-offset-2 col-sm-10">
					<input type="submit" class="btn btn-primary" value="등록하기" />
				</div>
			</div>

		</form>
	<div class="jumbotron">
		<div class="container">
	</div>
    </div>
                                </div>
                                </div>
                                </div>
                                </div>
                                </div>
                                </div>
  );
}


export default ProductRegistration;
