import React from 'react';
import { useNavigate } from 'react-router-dom';

const buttonStyle = {
  backgroundColor: 'orange',
  border: 'none',
  borderRadius: '8px',
  padding: '15px 30px',
  textDecoration: 'none',
  color: 'white',
  fontSize: '1.5em',
  transition: 'background-color 0.3s ease',
};

const hoverStyle = {
  backgroundColor: 'lightgray',
};
