import React from 'react';
import { useHistory } from 'react-router-dom';

const buttonStyle = {
  backgroundColor: 'orange',
  border: 'none',
  borderRadius: '8px',
  padding: '15px 30px',
  textDecoration: 'none',
  color: 'white',
  fontSize: '1.5em',
  transition: 'background-color 0.3s ease',
};

const hoverStyle = {
  backgroundColor: 'lightgray',
};

function Home() {
  const [isHovered, setIsHovered] = React.useState(false);
  const history = useHistory();
  const [isUserLoggedIn, setIsUserLoggedIn] = React.useState(false); // 로그인 상태 상태값

  // 로그인 여부 확인
  React.useEffect(() => {
    // 실제로는 로그인 여부를 확인하는 로직을 구현해야 합니다.
    // 예시로 사용자가 로그인되었다고 가정하겠습니다.
    setIsUserLoggedIn(true);
  }, []);

  // 상품 등록 버튼 클릭 핸들러
  const handleClick = () => {
    // 로그인되어 있지 않은 경우 로그인 페이지로 이동
    if (!isUserLoggedIn) {
      history.push('/login'); // 로그인 페이지 경로로 리다이렉트
    } else {
      history.push('/product-registration'); // 로그인된 경우 상품 등록 페이지로 이동
    }
  };

  return (
    <div>
      <button
        style={{ ...buttonStyle, ...(isHovered && hoverStyle) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onClick={handleClick}
      >
        상품 등록
      </button>
    </div>
  );
}

export default Home;
