import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useRecoilValue, useSetRecoilState } from 'recoil';
import { loginState } from '../recoils/login';

function Navbar() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();
  const loginUser = useRecoilValue(loginState);
  const setLoginUser = useSetRecoilState(loginState);

  useEffect(() => {
    if (loginUser) {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
    }
  }, [loginUser]);

  const handleLogout = () => {
    setLoginUser(null);
    setIsLoggedIn(false);
    navigate('/login');
  };

  const handleLogin = () => {
    setLoginUser(/* 유저 정보 입력 */); // 실제 로그인 정보로 업데이트
    setIsLoggedIn(true);
    navigate('/'); // 로그인 후 홈 화면으로 이동
  };

  return (
    <div className="d-flex m-2 me-0">
      {isLoggedIn || loginUser ? (
        <Link to="/" className="nav-item nav-link" onClick={handleLogout}>
          <sup>Logout</sup>
        </Link>
      ) : (
        <Link to="/login" className="nav-item nav-link" onClick={handleLogin}>
          <sup>Login</sup>
        </Link>
      )}
    </div>
  );
}

export default Navbar;
