  const paymentWindow = window.open(paymentURL, '_blank', 'width=600,height=400');

  // 팝업 창이 차단되었을 경우를 대비한 처리
  if (paymentWindow) {
    paymentWindow.focus();
  } else {
    alert('팝업 창이 차단되었습니다. 팝업 차단을 해제해주세요.');
  }
}

