// Shop.js

import React, { useState } from 'react';
import ProductRegistration from '../../components/products/ProductRegistration';

function Shop() {
  const [products, setProducts] = useState([]);

  const handleProductsChange = (updatedProducts) => {
    setProducts(updatedProducts);
  };

  return (
    <div>
      <ProductRegistration onProductsChange={handleProductsChange} />

      {/* 등록된 상품 목록 */}
      <div className="container mt-4">
        <h2 className="text-center">등록된 상품</h2>
        <div className="row">
          {products.map((product, index) => (
            <div key={index} className="border p-2 mb-3">
              {/* 상품 정보 표시 */}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Shop;
