import React from 'react';
import { Link } from 'react-router-dom';

const buttonStyle = {
  backgroundColor: 'orange',
  border: 'none',
  borderRadius: '8px',
  padding: '15px 30px',
  textDecoration: 'none',
  color: 'white',
  fontSize: '1.5em',
  boxShadow: '0px 3px 6px rgba(0, 0, 0, 0.1)', // 그림자 추가
  transition: 'box-shadow 0.3s ease', // 부드러운 변화를 위한 transition
};

const hoverStyle = {
  boxShadow: '0px 10px 12px rgba(0, 0, 0, 0.1)', // 마우스 호버시 그림자 크게
};

function Home() {
  const [isHovered, setIsHovered] = React.useState(false);

  return (
    <div>
      <Link
        to="/product-registration"
        style={{ ...buttonStyle, ...(isHovered && hoverStyle) }}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
      >
        상품 등록하기
      </Link>
    </div>
  );
}

export default Home;
