import React, { useEffect, useState } from 'react';

function ProductRegistration() {
  const [productName, setProductName] = useState('');
  const [productPrice, setProductPrice] = useState('');
  const [productDescription, setProductDescription] = useState('');
  const [productImage, setProductImage] = useState(null); // 이미지 상태 추가
  const [products, setProducts] = useState([]);

  const handleProductRegistration = (e) => {
    e.preventDefault();

  // 로컬 스토리지에서 상품 목록을 불러오는 함수
  const loadProductsFromLocalStorage = () => {
    const storedProducts = localStorage.getItem('products');
    if (storedProducts) {
      setProducts(JSON.parse(storedProducts));
    }
  };

  // 로컬 스토리지에 상품 목록을 저장하는 함수
  const saveProductsToLocalStorage = () => {
    localStorage.setItem('products', JSON.stringify(products));
  };

  useEffect(() => {
    loadProductsFromLocalStorage();
  }, []);

  useEffect(() => {
    saveProductsToLocalStorage();
  }, [products]);

    // 새로운 상품 객체를 생성하고 현재의 상품 목록에 추가
    const newProduct = {
      name: productName,
      price: productPrice,
      description: productDescription,
      image: productImage, // 이미지 추가
    };

    setProducts([...products, newProduct]);

    // 폼 초기화
    setProductName('');
    setProductPrice('');
    setProductDescription('');
    setProductImage(null); // 이미지 초기화
  };

  const handleImageChange = (e) => {
    const selectedImage = e.target.files[0];
    setProductImage(URL.createObjectURL(selectedImage));
  };

  return (
    <div>
      <h2>상품 등록</h2>
      <h2>상품 목록</h2>
      <h2>상품 목록</h2>
      <h2>상품 목록</h2>
      <h2>상품 목록</h2>
      <h2>상품 목록</h2>
      <h2>상품 목록</h2>

      <form onSubmit={handleProductRegistration}>
        <div>
          <label>상품명:</label>
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
          />
        </div>
        <div>
          <label>상품 가격:</label>
          <input
            type="text"
            value={productPrice}
            onChange={(e) => setProductPrice(e.target.value)}
          />
        </div>
        <div>
          <label>상품 설명:</label>
          <textarea
            value={productDescription}
            onChange={(e) => setProductDescription(e.target.value)}
          ></textarea>
        </div>
        <div>
          <label>이미지 업로드:</label>
          <input type="file" onChange={handleImageChange} accept="image/*" />
        </div>
        <button type="submit">등록</button>
      </form>

      {/* 등록된 상품 목록 */}
      <h2>상품 목록</h2>

      <div>
        {products.map((product, index) => (
          <div key={index}>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>{product.price}</p>
            <img src={product.image} alt={product.name} style={{ maxWidth: '500px' }} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductRegistration;
