import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
// import moment from 'moment';
import { useRecoilCallback, useRecoilValue } from 'recoil';

import { loginState } from '@recoils/login';
import { productState } from '@recoils/products'; // products로 변경

// import SubIntroSingle from '@components/SubIntroSingle';
// import UpdatePhotoModal from '@components/products/UpdatePhotoModal'; // users를 products로 변경

function ProductDetail() { // UserDetail을 ProductDetail로 변경
  const { id } = useParams();
  const navigate = useNavigate();

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const loginUser = useRecoilValue(loginState);
  const product = useRecoilValue(productState); // user를 product로 변경
  const getProductRecoil = useRecoilCallback(({ set }) => async (id) => {
    try {
      const resp = await axios.get('http://localhost:8000/products/' + id); // users를 products로 변경
      set(productState, resp.data.product); // user를 product로 변경
    } catch (error) {
      console.error(error.message);
    }
  }, []);

  useEffect(() => {
    getProductRecoil(id);
  }, [getProductRecoil, id]);

  return (
    <main id="main">
      {/* <UpdatePhotoModal show={show} handleClose={handleClose} id={product?.id}></UpdatePhotoModal>

      <SubIntroSingle title="상품 목록" sub="Product List" pathName="board"></SubIntroSingle> */}

      <section className="property-grid grid">
        <div className="container">
          <div className="row">
            <div className="card p-2" style={{ width: '18rem' }}>
              <img src={product?.imagePath} className="card-img-top" alt="..." /> {/* profileImagePath를 imagePath로 변경 */}
              <div className="card-body">
                <h5 className="card-title">{product?.name}</h5>
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>

                {(loginUser.email === product.email) &&
                  <Link className="btn btn-outline-secondary" onClick={handleShow}>이미지 수정</Link>
                }
              </div>
            </div>

            <div className="col-sm-8">
              <table className="table mb-5">
                <tbody>
                  <tr>
                    <th style={{ width: '10rem' }}>이름</th>
                    <td>{product?.name}</td>
                  </tr>
                  <tr>
                    <th>이메일</th>
                    <td>{product?.email}</td>
                  </tr>
                  {/* 나머지 속성들도 product에 맞게 변경 */}
                </tbody>
              </table>

              <div className="text-end">
                <button className="btn btn-outline-secondary" onClick={() => navigate('/productList')}>리스트</button>{' '}

                {(loginUser.email === product.email) &&
                  <>
                    <button className="btn btn-outline-secondary" onClick={() => navigate('/productUpdate')}>수정</button> {' '}
                    <button className="btn btn-outline-secondary" onClick={() => navigate('/productUpdate')}>삭제</button> {' '}
                  </>
                }
              </div>
            </div>
          </div>
        </div>
      </section>
    </main>
  )
}
export default ProductDetail;

ProductDetail.defaultProps = {
  sub: ''
};
